/*!mycat:sql=select id from wm_fans_statistics where burst='${burst}'*/ select from wm_fans_statistics
user_id = #{userId}
and date(created_time) = CURDATE()
and date(created_time) >= date(#{dto.stime})
and date(created_time) <= date(#{dto.etime})
